package com.android.chefshare

import android.content.Intent
import android.graphics.Rect
import android.os.Bundle
import android.view.View
import android.widget.TextView
import android.widget.Toast
import android.widget.ImageView
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.GravityCompat
import androidx.drawerlayout.widget.DrawerLayout
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.google.android.material.floatingactionbutton.FloatingActionButton
import com.google.android.material.navigation.NavigationView

class MyRecipeStorageActivity : AppCompatActivity() {

    private lateinit var rvSavedRecipes: RecyclerView
    private lateinit var rvRecentRecipes: RecyclerView
    private lateinit var tvSavedCount: TextView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        val currentUser = com.google.firebase.auth.FirebaseAuth.getInstance().currentUser
        if (currentUser == null) {
            startActivity(Intent(this, LoginActivity::class.java))
            finish()
            return
        }

        setContentView(R.layout.activity_my_recipe_storage)

        // Ánh xạ view
        rvSavedRecipes  = findViewById(R.id.rvSavedRecipes)
        rvRecentRecipes = findViewById(R.id.rvRecentRecipes)
        tvSavedCount = findViewById(R.id.tvSavedCount)
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottomNavigation)

        // DrawerLayout + NavigationView
        val drawerLayout = findViewById<DrawerLayout>(R.id.drawerLayout)
        val navigationView = findViewById<NavigationView>(R.id.navigationView)
        val imgHeaderAvatar = findViewById<ImageView>(R.id.imgHeaderAvatar)

        val user = UserManager.currentUser
        val photoUrl = user?.photoUrl?.toString()

        if (!photoUrl.isNullOrEmpty()) {
            imgHeaderAvatar.visibility = View.VISIBLE
            Glide.with(this)
                .load(photoUrl)
                .placeholder(R.drawable.ic_person)
                .circleCrop()
                .into(imgHeaderAvatar)
        } else {
            imgHeaderAvatar.visibility = View.VISIBLE
            imgHeaderAvatar.setImageResource(R.drawable.ic_person) // Dùng icon thay thế
        }

        val fabAddRecipe = findViewById<FloatingActionButton>(R.id.fabAddRecipe)
        fabAddRecipe.setOnClickListener {
            val intent = Intent(this, UploadRecipeActivity::class.java)
            startActivity(intent)
        }

        // Dữ liệu mẫu - gần đây
        val recentRecipes = listOf(
            Recipe("Lẩu thái", "data:image/jpeg;base64,"),
            Recipe("Gỏi cuốn", "data:image/jpeg;base64,"),
            Recipe("Mực nướng", "data:image/jpeg;base64,")
        )

        // Dữ liệu mẫu - đã lưu
        val savedRecipes = listOf(
            Recipe("Mì Ý bò bằm", "data:image/jpeg;base64,"),
            Recipe("Sườn nướng mật ong", "data:image/jpeg;base64,"),
            Recipe("Cơm gà", "data:image/jpeg;base64,"),
            Recipe("Trà sữa", "data:image/jpeg;base64,")
        )
        tvSavedCount.text = "${savedRecipes.size} món đã lưu"

        // Gán adapter cho RecyclerView - đã lưu
        rvSavedRecipes.layoutManager = GridLayoutManager(this, 3) //số lượng cột/ món trong 1 hàng
        rvSavedRecipes.adapter = RecipeAdapter(savedRecipes)
        rvSavedRecipes.addItemDecoration(object : RecyclerView.ItemDecoration() {
            private val space = 32 // pixel
            override fun getItemOffsets(
                outRect: Rect,
                view: View,
                parent: RecyclerView,
                state: RecyclerView.State
            ) {
                outRect.left = space / 2
                outRect.right = space / 2
                outRect.bottom = space
            }
        })

        // Gán adapter cho RecyclerView - gần đây
        rvRecentRecipes.layoutManager = LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
        rvRecentRecipes.adapter = RecipeAdapter(recentRecipes)
        rvRecentRecipes.addItemDecoration(object : RecyclerView.ItemDecoration() {
            private val space = 20 // pixel
            override fun getItemOffsets(
                outRect: Rect,
                view: View,
                parent: RecyclerView,
                state: RecyclerView.State
            ) {
                outRect.right = space
                if (parent.getChildAdapterPosition(view) == 0) {
                    outRect.left = space
                }
            }
        })

        // Bottom nav
        bottomNavigation.selectedItemId = R.id.nav_storage
        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_home -> {
                    val intent = Intent(this, MainActivity::class.java)
                    intent.putExtra("SELECTED_TAB", R.id.nav_home)
                    intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP or Intent.FLAG_ACTIVITY_SINGLE_TOP
                    startActivity(intent)
                    true
                }

                R.id.nav_ai -> {
                    val intent = Intent(this, AIRecipeActivity::class.java)
                    intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP
                    startActivity(intent)
                    true
                }

                R.id.nav_storage -> true
                else -> false
            }
        }

        // Gán thông tin user vào header view của Navigation Drawer
        val headerView = navigationView.getHeaderView(0)
        bindUserHeader(headerView, drawerLayout, navigationView)

        // Gán thông tin user vào header trên cùng của màn home
        bindTopHeader()

        navigationView.setNavigationItemSelectedListener { menuItem ->
            when (menuItem.itemId) {
                R.id.nav_profile -> {
                    val intent = Intent(this, ProfileActivity::class.java)
                    startActivity(intent)
                }
                R.id.nav_friends -> showToast("Các Bạn Bếp")
                R.id.nav_stats -> showToast("Thống Kê Bếp")
                R.id.nav_recent -> showToast("Món đã xem gần đây")
                R.id.nav_premium -> showToast("Premium")
                R.id.nav_challenges -> showToast("Thử Thách")
                R.id.nav_settings -> {
                    val intent = Intent(this, SettingsActivity::class.java)
                    startActivity(intent)
                }

                R.id.nav_faq -> showToast("Câu hỏi thường gặp")
                R.id.nav_feedback -> showToast("Gửi phản hồi")
            }
            drawerLayout.closeDrawer(navigationView)
            true
        }
    }

    private fun showToast(message: String) {
        Toast.makeText(this, message, Toast.LENGTH_SHORT).show()
    }

    private fun bindUserHeader(
        header: View,
        drawerLayout: DrawerLayout,
        navigationView: NavigationView
    ) {
        val imgAvatar = header.findViewById<ImageView>(R.id.imgAvatar)
        val tvUsername = header.findViewById<TextView>(R.id.tvUsername)
        val tvHandle = header.findViewById<TextView>(R.id.tvHandle)

        val user = UserManager.currentUser
        val context = this

        tvUsername.text = user?.displayName ?: "Ẩn danh"
        tvHandle.text = user?.email ?: "Không rõ"

        val photoUrl = user?.photoUrl?.toString()
        if (!photoUrl.isNullOrEmpty()) {
            Glide.with(context)
                .load(photoUrl)
                .placeholder(R.drawable.ic_person) // icon hiển thị trong lúc chờ load
                .circleCrop()
                .into(imgAvatar)
        } else {
            imgAvatar.setImageResource(R.drawable.ic_person) //icon khi không có avatar
        }

    }

    private fun bindTopHeader() {
        val imgHeaderAvatar = findViewById<ImageView>(R.id.imgHeaderAvatar)
        val drawerLayout = findViewById<DrawerLayout>(R.id.drawerLayout)
        val navigationView = findViewById<NavigationView>(R.id.navigationView)

        val user = UserManager.currentUser
        val photoUrl = user?.photoUrl?.toString()

        imgHeaderAvatar.visibility = View.VISIBLE

        if (!photoUrl.isNullOrEmpty()) {
            Glide.with(this)
                .load(photoUrl)
                .placeholder(R.drawable.ic_person)
                .circleCrop()
                .into(imgHeaderAvatar)
        } else {
            imgHeaderAvatar.setImageResource(R.drawable.ic_person) // 👈 icon nếu không có ảnh
        }

        imgHeaderAvatar.setOnClickListener {
            drawerLayout.openDrawer(navigationView)
        }
    }

}