package com.android.chefshare

import android.os.Bundle
import android.widget.ImageButton
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView

class RecentDishesActivity : AppCompatActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_recent_dishes)

        val btnBack = findViewById<ImageButton>(R.id.btnBack)
        btnBack.setOnClickListener {
            finish()
        }

        val recyclerView = findViewById<RecyclerView>(R.id.recyclerRecentDishes)
        recyclerView.layoutManager = LinearLayoutManager(this)

        // Danh sách món ăn mẫu với ảnh từ internet
        val sampleDishes = listOf(
            Dish(
                "Mực (nang) một nắng",
                "mực nang (1kg), muối ớt xanh...",
                "Bếp của Quỳnh",
                "data:image/jpeg;base64,"
            ),
            Dish(
                "Cơm một mình",
                "Canh cải thìa, chả chiên, cơm trắng...",
                "Annie Vo",
                "data:image/jpeg;base64,"
            ),
            Dish(
                "Canh bí đỏ nấu tôm",
                "Tôm, bí đỏ, hành lá, ngò...",
                "Quán ăn Chú Bảy",
                "data:image/jpeg;base64,"
            )
        )

        recyclerView.adapter = DishAdapter(sampleDishes)
    }
}
