package com.android.chefshare

import android.content.Intent
import android.os.Bundle
import android.util.Log
import android.view.View
import android.widget.ImageButton
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.google.android.material.floatingactionbutton.FloatingActionButton

class SearchResultsActivity : AppCompatActivity() {
    private lateinit var recyclerView: RecyclerView
    private lateinit var adapter: SearchResultAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        Log.d("DebugCheck", "SearchResultsActivity created")
        setContentView(R.layout.activity_search_results)

        //sự kiện cho back
        val btnBack = findViewById<ImageButton>(R.id.btnBack)
        btnBack.setOnClickListener {
            finish()
        }

        val keyword = intent.getStringExtra("keyword") ?: ""
        recyclerView = findViewById(R.id.rvSearchResults)
        recyclerView.layoutManager = GridLayoutManager(this, 1) // 1 cột giống ảnh demo

        // Danh sách mẫu dữ liệu
        val allResults = listOf(
            SearchResult(
                "Gà nấu khoai tây",
                "Đùi gà góc tư, khoai tây lớn, hành tím băm...",
                "PKL",
                "data:image/jpeg;base64,"
            ),
            SearchResult(
                "Gà sốt chua ngọt Jikhoba",
                "Thịt gà không xương, mọi người giảm lại định lượng nhé...",
                "Yuri",
                "data:image/jpeg;base64,"
            ),
            SearchResult(
                "Cánh gà nướng",
                "Cánh gà, Sốt thịt nướng Cholimex",
                "Vo",
                "data:image/jpeg;base64,"
            )
        )

        // Lọc theo keyword
        val results = allResults

        adapter = SearchResultAdapter(results)
        recyclerView.adapter = adapter

        val fab = findViewById<View>(R.id.fabContainer)
        val fabAddRecipe = fab.findViewById<FloatingActionButton>(R.id.fabAddRecipe)
        fabAddRecipe.setOnClickListener {
            val intent = Intent(this, UploadRecipeActivity::class.java)
            startActivity(intent)
        }

        // Bottom nav
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottomNavigation)
        bottomNavigation.selectedItemId = R.id.nav_storage
        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_home -> {
                    val intent = Intent(this, MainActivity::class.java)
                    intent.putExtra("SELECTED_TAB", R.id.nav_home)
                    intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP or Intent.FLAG_ACTIVITY_SINGLE_TOP
                    startActivity(intent)
                    true
                }
                R.id.nav_ai -> {
                    Toast.makeText(this, "Tính năng AI đang phát triển", Toast.LENGTH_SHORT).show()
                    true
                }
                R.id.nav_storage -> true
                else -> false
            }
        }
    }
}
